import React from "react";
import ReactDOM from "react-dom/client";
import "./index.css";
import App from "./who-wants-to-be-millionaire/adapters/primary/react/components/App";
import reportWebVitals from "./reportWebVitals";
import { Provider } from "react-redux";
import { initReduxStore } from "./who-wants-to-be-millionaire/store/reduxStore";
import { FakeCurrentQuestionGateway } from "./who-wants-to-be-millionaire/adapters/secondary/gateways/current-gateway/fake/fakeCurrentQuestionGateway";
import { RandomArrayIndexPicker } from "./who-wants-to-be-millionaire/adapters/secondary/gateways/current-gateway/real/randomArrayIndexNumber";
import { AnswerLetter } from "./who-wants-to-be-millionaire/store/appState";

const questionsPool = [
  {
    id: "123abc",
    label: "Que signifie l'acronyme TDD ?",
    answersOptions: {
      A: "Toto-Driven Development",
      B: "Test-Driven Development",
      C: "Test-Development Driven",
      D: "La réponse D",
    },
  },
  {
    id: "456def",
    label: "Que signifie l'acronyme CQRS ?",
    answersOptions: {
      A: "Cool Query Responsibility Segregation",
      B: "Command Quiz Responsibility Segregation",
      C: "Command Query Responsibility Segregation",
      D: "La réponse D",
    },
  },
];

const correctAnswerByQuestionId: Record<string, AnswerLetter> = {
  "123abc": "B",
  "456def": "C",
};

const currentQuestionGateway = new FakeCurrentQuestionGateway(
  new RandomArrayIndexPicker(),
  questionsPool,
  correctAnswerByQuestionId
);
const store = initReduxStore({ currentQuestionGateway });

const root = ReactDOM.createRoot(
  document.getElementById("root") as HTMLElement
);

root.render(
  <React.StrictMode>
    <Provider store={store}>
      <App />
    </Provider>
  </React.StrictMode>
);

// If you want to start measuring performance in your app, pass a function
// to log results (for example: reportWebVitals(console.log))
// or send to an analytics endpoint. Learn more: https://bit.ly/CRA-vitals
reportWebVitals();
