import { AnyAction } from "@reduxjs/toolkit";

export const currentQuestionRetrievalReducer = (
  state: { data: string | null } = { data: null },
  action: AnyAction
) => {
  if (action.type === "CURRENT_QUESTION_RETRIEVED")
    return { data: action.payload };
  return state;
};
