import React, { FC } from "react";
import { Question } from "./core-logic/usecases/current-question-retrieval/question";

type Props = {
  answersOptions: Question["answersOptions"];
};

export const AnswersOptions: FC<Props> = ({ answersOptions }) => {
  return (
    <div className="w-full justify-center grid grid-cols-2 text-white gap-4 font-mono text-sm text-left font-bold leading-6 bg-stripes-fuchsia rounded-lg">
      {Object.entries(answersOptions).map(([letter, label]) => {
        return (
          <div
            key={letter}
            className="border-3 border-blue-300 rounded-lg px-3 py-1 bg-gray-900"
          >
            <span className="text-orange-500">{letter}:</span> {label}
          </div>
        );
      })}
    </div>
  );
};
